(ns bathroom-code.core)

(def test-data ["ULL"
                "RRDDD"
                "LURDL"
                "UUUUD"])

(def puzzle-input ["" 
                   ""
                   "" 
                   ""
                   ""])

(def std-keypad [[1 2 3]
                 [4 5 6]
                 [7 8 9]])

(def funky-keypad
  [[nil nil 1 nil nil]
   [nil 2 3 4 nil]
   [5 6 7 8 9]
   [nil \A \B \C nil]
   [nil nil \D nil nil]])

(defn value-from [keypad [x y]] (-> (get keypad y) (get x)))
(defn direction->movement [direction]
  (case direction
    \U [0 -1]
    \L [-1 0]
    \R [1 0]
    \D [0 1]
    (throw (IllegalArgumentException. (str "Invalid direction: " direction)))))

(defn move [keypad [x y :as current] direction]
  (let [movement (direction->movement direction)
        next-coord (mapv + current movement)
        next-value (value-from keypad next-coord)]
    (if (nil? next-value) current next-coord)))

(defn follow-directions
  [keypad [x y :as position] directions]
  (if (empty? directions)
    position
    (recur keypad
           (move keypad position (first directions))
           (rest directions))))

(defn reducer-fn [{:keys [keypad position visited] :as state}
                  directions]
  (let [new-position (follow-directions keypad position directions)]
    (-> state
        (assoc :position new-position)
        (assoc :visited (conj visited new-position)))))

(defn crack-the-code [keypad [x y :as starting-point] instructions]
  (let [visited (:visited (reduce reducer-fn {:keypad keypad
                                              :position starting-point
                                              :visited []} (map seq instructions)))]
    (apply str (map #(value-from keypad %) visited))))
